import React from 'react';
import { Xenon, UserCredentials, Role } from '@anciitk/xenon-js';
import './styles.module.css';
import { V0alpha1Api, Session } from '@ory/kratos-client';
export interface SessionState {
    active: boolean;
    logoutUrl: string;
    user: UserCredentials;
    session: Session;
}
interface Props {
    loginUrl: string;
    path: string;
    basePath: string;
    historyPush: (route: string) => void;
    sessionState: SessionState | undefined;
    setSessionState: (session: SessionState) => void;
    ory: V0alpha1Api;
    xenon: Xenon;
    roles: Array<Role>;
}
declare const VerifyPage: React.FC<Props>;
export default VerifyPage;
